package com.xwc.shpay.config;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.web.context.request.async.DeferredResult;import springfox.documentation.builders.ApiInfoBuilder;import springfox.documentation.builders.RequestHandlerSelectors;import springfox.documentation.service.ApiInfo;import springfox.documentation.service.Contact;import springfox.documentation.spi.DocumentationType;import springfox.documentation.spring.web.plugins.Docket;import springfox.documentation.swagger2.annotations.EnableSwagger2;/** * @author jack */@Configuration@EnableSwagger2public class SwaggerConfiguration {    @Bean    public SwaggerProperties swaggerProperties() {        return new SwaggerProperties();    }    @Bean    public Docket createRestApi() {        SwaggerProperties properties = swaggerProperties();        return new Docket(DocumentationType.SWAGGER_2)                .apiInfo(apiInfo(properties))                .genericModelSubstitutes(DeferredResult.class)                .useDefaultResponseMessages(false)                .forCodeGeneration(true)                .pathMapping(properties.getPathMapping())                .select()                .apis(RequestHandlerSelectors.basePackage(properties.getBasePackage()))                .build();    }    private ApiInfo apiInfo(SwaggerProperties properties) {        return new ApiInfoBuilder()                .title(properties.getApiTitle())                .description(properties.getApiDescription())                .termsOfServiceUrl(properties.getApiTermsOfServiceUrl())                .contact(new Contact(properties.getApiContactName(),                        properties.getApiContactURL(),                        properties.getApiContactEmail()))                .version(properties.getApiVersion())                .build();    }}